import React, { useState, useEffect, useCallback } from 'react';
import {
  View,
  StyleSheet,
  ScrollView,
  RefreshControl,
  Alert,
} from 'react-native';
import {
  Text,
  Card,
  Button,
  Avatar,
  Chip,
  FAB,
} from 'react-native-paper';
import { Ionicons } from '@expo/vector-icons';
import AsyncStorage from '@react-native-async-storage/async-storage';

import { authAPI, coupleAPI, pointsAPI } from '../services/api';
import { colors, commonStyles } from '../styles/theme';
import { useSnackbar } from '../hooks/useSnackbar';
import { showError, showSuccess, showInfo } from '../utils/notifications';
import CustomSnackbar from '../components/CustomSnackbar';

export default function HomeScreen({ navigation }) {
  const [userInfo, setUserInfo] = useState(null);
  const [coupleInfo, setCoupleInfo] = useState(null);
  const [points, setPoints] = useState(0);
  const [recentHistory, setRecentHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);

  const snackbar = useSnackbar();

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      // 从本地存储获取用户信息
      const storedUserInfo = await AsyncStorage.getItem('userInfo');
      if (storedUserInfo) {
        setUserInfo(JSON.parse(storedUserInfo));
      }

      // 获取最新的用户资料
      const profileResponse = await authAPI.getProfile();
      setUserInfo(profileResponse.user);
      setPoints(profileResponse.user.points);

      // 获取情侣信息
      try {
        const coupleResponse = await coupleAPI.getCouple();
        setCoupleInfo(coupleResponse.couple);
      } catch (error) {
        if (error.response?.status !== 404) {
          console.error('获取情侣信息失败:', error);
        }
      }

      // 获取最近的积分历史
      try {
        const historyResponse = await pointsAPI.getHistory(5, 0);
        setRecentHistory(historyResponse.history || []);
      } catch (error) {
        console.error('获取积分历史失败:', error);
      }
    } catch (error) {
      console.error('加载数据失败:', error);
      snackbar.showError('加载数据失败，请检查网络连接');
      showError('加载数据失败，请检查网络连接');
    } finally {
      setLoading(false);
    }
  };

  const onRefresh = useCallback(async () => {
    setRefreshing(true);
    await loadData();
    setRefreshing(false);
  }, []);

  const handleInviteCouple = () => {
    Alert.prompt(
      '邀请情侣',
      '请输入对方的用户名',
      [
        { text: '取消', style: 'cancel' },
        {
          text: '邀请',
          onPress: async (username) => {
            if (!username?.trim()) return;

            try {
              await coupleAPI.invite(username.trim());
              Alert.alert('成功', '邀请发送成功！');
              loadData(); // 重新加载数据
            } catch (error) {
              const errorMessage = error.response?.data?.error || '邀请失败';
              Alert.alert('邀请失败', errorMessage);
            }
          },
        },
      ],
      'plain-text'
    );
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('zh-CN', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const getPointsColor = (points) => {
    if (points > 0) return colors.success;
    if (points < 0) return colors.error;
    return colors.onSurfaceVariant;
  };

  if (loading) {
    return (
      <View style={commonStyles.centerContainer}>
        <Text>加载中...</Text>
      </View>
    );
  }

  return (
    <View style={commonStyles.container}>
      <ScrollView
        style={styles.scrollView}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
      >
        {/* 用户信息卡片 */}
        <Card style={styles.userCard}>
          <Card.Content style={styles.userCardContent}>
            <Avatar.Icon
              size={60}
              icon="account"
              style={{ backgroundColor: colors.primary }}
            />
            <View style={styles.userInfo}>
              <Text style={styles.username}>{userInfo?.username}</Text>
              <View style={styles.pointsContainer}>
                <Ionicons name="diamond" size={20} color={colors.coin} />
                <Text style={styles.pointsText}>{points} 积分</Text>
              </View>
            </View>
          </Card.Content>
        </Card>

        {/* 情侣信息卡片 */}
        {coupleInfo ? (
          <Card style={[commonStyles.card, styles.coupleCard]}>
            <Card.Content>
              <View style={styles.coupleHeader}>
                <Ionicons name="heart" size={24} color={colors.heart} />
                <Text style={styles.coupleTitle}>我的情侣</Text>
              </View>
              <View style={styles.coupleInfo}>
                <Avatar.Icon
                  size={50}
                  icon="account-heart"
                  style={{ backgroundColor: colors.secondary }}
                />
                <View style={styles.partnerInfo}>
                  <Text style={styles.partnerName}>
                    {coupleInfo.partner.username}
                  </Text>
                  <View style={styles.partnerPoints}>
                    <Ionicons name="diamond" size={16} color={colors.coin} />
                    <Text style={styles.partnerPointsText}>
                      {coupleInfo.partner.points} 积分
                    </Text>
                  </View>
                </View>
              </View>
            </Card.Content>
          </Card>
        ) : (
          <Card style={[commonStyles.card, styles.noCoupleCard]}>
            <Card.Content style={styles.noCoupleContent}>
              <Ionicons name="heart-outline" size={48} color={colors.onSurfaceVariant} />
              <Text style={styles.noCoupleText}>还没有情侣</Text>
              <Text style={styles.noCoupleSubtext}>邀请你的另一半加入吧！</Text>
              <Button
                mode="contained"
                onPress={handleInviteCouple}
                style={styles.inviteButton}
                icon="heart-plus"
              >
                邀请情侣
              </Button>
            </Card.Content>
          </Card>
        )}

        {/* 最近活动 */}
        {recentHistory.length > 0 && (
          <Card style={commonStyles.card}>
            <Card.Content>
              <Text style={styles.sectionTitle}>最近活动</Text>
              {recentHistory.map((item) => (
                <View key={item.id} style={styles.historyItem}>
                  <View style={styles.historyLeft}>
                    <Text style={styles.historyDescription}>
                      {item.description}
                    </Text>
                    <Text style={styles.historyDate}>
                      {formatDate(item.created_at)}
                    </Text>
                  </View>
                  <Chip
                    style={[
                      styles.pointsChip,
                      { backgroundColor: getPointsColor(item.points) + '20' },
                    ]}
                    textStyle={{ color: getPointsColor(item.points) }}
                  >
                    {item.points > 0 ? '+' : ''}{item.points}
                  </Chip>
                </View>
              ))}
            </Card.Content>
          </Card>
        )}
      </ScrollView>

      {/* 浮动操作按钮 */}
      {coupleInfo && (
        <FAB
          icon="plus"
          style={styles.fab}
          onPress={() => {
            // 这里可以添加快速操作菜单
            Alert.alert(
              '快速操作',
              '选择要执行的操作',
              [
                { text: '取消', style: 'cancel' },
                { text: '创建事件', onPress: () => navigation.navigate('Events') },
                { text: '执行规则', onPress: () => navigation.navigate('Rules') },
                { text: '购买商品', onPress: () => navigation.navigate('Shop') },
              ]
            );
          }}
        />
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  scrollView: {
    flex: 1,
  },
  userCard: {
    ...commonStyles.card,
    backgroundColor: colors.primaryContainer,
  },
  userCardContent: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  userInfo: {
    marginLeft: 16,
    flex: 1,
  },
  username: {
    fontSize: 20,
    fontWeight: 'bold',
    color: colors.onPrimaryContainer,
  },
  pointsContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginTop: 4,
  },
  pointsText: {
    fontSize: 16,
    fontWeight: '600',
    color: colors.onPrimaryContainer,
    marginLeft: 4,
  },
  coupleCard: {
    backgroundColor: colors.secondaryContainer,
  },
  coupleHeader: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 12,
  },
  coupleTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    color: colors.onSecondaryContainer,
    marginLeft: 8,
  },
  coupleInfo: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  partnerInfo: {
    marginLeft: 12,
  },
  partnerName: {
    fontSize: 16,
    fontWeight: '600',
    color: colors.onSecondaryContainer,
  },
  partnerPoints: {
    flexDirection: 'row',
    alignItems: 'center',
    marginTop: 4,
  },
  partnerPointsText: {
    fontSize: 14,
    color: colors.onSecondaryContainer,
    marginLeft: 4,
  },
  noCoupleCard: {
    backgroundColor: colors.surfaceVariant,
  },
  noCoupleContent: {
    alignItems: 'center',
    paddingVertical: 20,
  },
  noCoupleText: {
    fontSize: 18,
    fontWeight: 'bold',
    color: colors.onSurfaceVariant,
    marginTop: 12,
  },
  noCoupleSubtext: {
    fontSize: 14,
    color: colors.onSurfaceVariant,
    marginTop: 4,
    marginBottom: 16,
  },
  inviteButton: {
    backgroundColor: colors.primary,
  },
  sectionTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    color: colors.onSurface,
    marginBottom: 12,
  },
  historyItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingVertical: 8,
    borderBottomWidth: 1,
